
CREATE DATABASE /*!32312 IF NOT EXISTS*/`serious` /*!40100 DEFAULT CHARACTER SET utf8 */;

USE `serious`;

/*Table structure for table `movie_comment` */

DROP TABLE IF EXISTS `movie_comment`;

CREATE TABLE `movie_comment` (
  `id` int(11) NOT NULL AUTO_INCREMENT,
  `moviename` varchar(50) DEFAULT NULL COMMENT '电影名称',
  `avater` varchar(20) DEFAULT NULL COMMENT '评论人',
  `comment` varchar(500) DEFAULT NULL COMMENT '评论',
  PRIMARY KEY (`id`)
) ENGINE=InnoDB AUTO_INCREMENT=402 DEFAULT CHARSET=utf8mb4;

/*Data for the table `movie_comment` */

insert  into `movie_comment`(`id`,`moviename`,`avater`,`comment`) values (202,'罗马 Roma(2018)','陀螺凡达可','不同文本层次相互作用交融的同时共同承载了创作者对墨西哥对童年最私密最个人的回忆和情感，这种感性、私人同时又宏大的影像力量喷薄而出，犹如影片最后那一波又一波的巨浪不断冲击并试图淹没大银幕前的每一个观众。'),(203,'罗马 Roma(2018)','二十二岛主','威尼斯第4场主竞赛。近年来看到的最细腻的黑白影像，很喜欢，本以为会是乡愁故事，原来是一曲女性悲歌。以女佣视角为切入点，来展现记忆中的女性生活，但很多生存困境直到今天仍然没有改变。从洗刷地上的泡沫，到海边的惊涛巨浪，女主与她的主人一家人实现了精神上的涅槃，虽然人活着终究如逃不开大门的狗，但只要有希望，就一定还能走下去。至于男性，还是就在光着屁股耍棍子和踩狗屎中自我意淫吧。'),(204,'罗马 Roma(2018)','米粒','1971年墨西哥政府镇压革命的时候，在暴力和枪火中，怀孕的年轻保姆因为耽搁了救治而失去了孩子。本来这是个非常好的故事，社会动荡与个人命运在一个时间点交汇会产生巨大的能量；但正如《你妈妈也一样》，到这部《罗马》，卡隆的富家公子哥身份和视角限制了他的政治敏感度。他当然觉得暴力行为很残酷，但他的角度是“我不喜欢这些，它影响了我平静富足的生活，让我失去了朋友，让母亲失去了孩子”。所以，故事仅停留在“渣男本渣”后试图以阶级融合的方式展现女性主义，是比较表面，也不够有说服力的。（比如：不同阶层的女性的悲剧只是因为男的“渣”吗？）当然视觉、听觉上，这部片很好'),(205,'罗马 Roma(2018)','九只苍蝇撞墙','故作镇静的匠气横生。特别是当想起《风烛泪》中的怀孕女仆或者《海上花》中的平移镜头，更觉《罗马》的平庸。本质上，卡隆对那个时代墨西哥的整体动荡对于人个体情感意识的深层次影响并没有多少发自内心的认知，也正是因此他把精力全都放在了奇观化的场景塑造上，无论是印在地板砖上的飞机倒影，还是学血流成河的街头运动，从炮筒中蹿出的空中飞人还是汹涌吞噬的海浪，都仅仅起到了活报剧的马戏效应。特别是还因为要展示几个奇观把画幅拉伸到毫无必要的2.35:1，导致大部分画面都是摄影机反复摇来晃去而毫无新鲜内容，看的极其疲惫。卡隆本质还是一个好莱坞导演，不具备自我表达时的理性思维深度。'),(206,'罗马 Roma(2018)','咯咯精','有生皆苦！哭太忘我了。那么好，是充分必要，也是他不最在意的，让更多人看到。//大银幕二刷三刷。Netflix四刷。唾弃人，相信人，怜悯人，哭到岔气。'),(207,'罗马 Roma(2018)','PeterCat','《罗马》让人困惑之处在于，这个与导演紧密相连的“童年往事”却出奇的抽离，缺乏一个介入过去的有效视角。从私人记忆角度来看，影片无疑太冷峻太客观；而从历史叙事视角来讲，《罗马》格局又太个人太细枝末节，尤其是游离在家庭和社会之中的女仆部分，充当隐喻的设计感太强。在革命日再次遭遇孩子的父亲，还紧接着产子，怎么听都像是中国坊间盛传那种某事件一句话小说的格局。'),(208,'罗马 Roma(2018)','何阿嵐','散文敘事並不會構成「弱影像」的主因，反之，是一步一步增量情感的濃度，讓觀眾慢慢放下對情節的追求，全然走入/體驗人物內心，身處在時代和空間之中，觀眾有時成為旁觀者，又時成為參與者。但《Roma》只不過是形式上的模仿犯，剪接和調度都是電視影像，所有事情都放在一個平面上，要讓觀眾明明白白，時間控制感和敘事線更是最愚笨的連結(上一場顧主生氣發洩要工人主角執狗屎，下一場又真的去執狗屎的鏡頭)，就算在電視和大銀幕根本分別不大，但黑白又令影像過份冷靜，Alfonso真的深入梳理70年代的墨西哥面貌嗎？很多時候更不知道為何移動，似是為了長鏡頭形式而破壞了信息和故事量，他最多做到橫向層面，深入人物內心情感幾乎全面失敗，工人和孩子們的關係為何深入呢？如荷里活式的淺薄，在Iosseliani，Olmi，Tati和侯孝賢面前，Alfonso根本不值一題。'),(209,'罗马 Roma(2018)','McLwren','SOPOWERFUL!ACADEMYAWARD!!!其实这部片让我想到前不久看的Zama，有不少相似之处，包括个体与大背景之间的融合关系和对自然的景象捕捉，但是各异之处又令我感到同质的震撼，两部片年度Top3。'),(210,'罗马 Roma(2018)','蜉蝣','抱着15米巨浪的期待，看到的是2米高的波澜。'),(211,'罗马 Roma(2018)','LORENZO','《罗马》是私人的，但它依然拍出了时代的宏大。虽然少许剧情有些肥皂剧的嫌疑，然而在它连贯的、通达的影像光辉之下，这一封卡隆的私人家书闪耀着史诗的光芒，它或许是卡隆导演生涯的巅峰。女主角Cleo的命运是对墨西哥大背景时代变迁的无声证言，加上男性角色的缺席，电影笼罩着女权主义的光环。以这样深情的角度去诠释自己生命中一个极度重要的时刻，去惦念划过自己生命的重要的人，或许是每一个导演的终极梦想。正如卡隆所说：“电影中百分之90的场景来自于我的记忆，有些直接，有些则迂回，但都是关于那个塑造我的时代，或者说塑造墨西哥的时代，那正是墨西哥漫长时代变迁的起始点。”'),(212,'罗马 Roma(2018)','南乌','不如《你妈妈也一样》，当年卡隆维持了个人故事和革命之间的微妙距离。现在则是枪和羊水的相会，太剥削了。影片一直在两股势力的交锋中挣扎，一是卡隆的本质上对“运动”的狂热，包括镜头运动和时代变动，在他最得意的镜头里，人物各行其轨道，形成完美的星系。二是影片勉力维持的女仆的静观视角。前半小时聚焦女仆的日常工作，却总用舞蹈式的环绕调度来进行，近乎戈达尔中期作品，非常分裂。影片进入中段后观感提升，因为不再强行进入日常和微观，而是以情节去建构。最后的高潮以静观向动作的投降告终，Cleo要搏击海浪才能维持仆人之名。这不是Cleo的胜利，这是海浪的胜利。卡隆对这里隐藏的反讽性视而不见。他还是适合拍大情节作品，《地心引力》多好。'),(213,'罗马 Roma(2018)','robertooo','第一个室内横摇长镜就想拜倒所有的技术都臣服于感情力度表达海浪击穿我的鼓膜你无家可归//二刷即便全是卡隆有意编织的情感漩涡又如何呢我甘愿受困Atelier'),(214,'罗马 Roma(2018)','把噗','不神气。你们有《罗马》，我们有《八月》；我们有无可匹敌的《悲情城市》，你们没有。'),(215,'罗马 Roma(2018)','凌睿','这人世间的路太窄，不仅会处处碰壁，撞得遍体鳞伤，还会时不时踩到几坨狗屎。但是，我们还可以去海边，洗尽铅华和晦气。天边，夕阳正冉冉升起，燃烧着希望和力量。'),(216,'罗马 Roma(2018)','同志亦凡人中文站','女仆故事，史诗气质。阿方索·卡隆贡献了从影以来最稳的一次掌镜，一组组镜头气度庄严，徐徐推开墨西哥“罗马”社区的家庭社会画卷。难得的是平淡中蕴含张力，最后的海浪画面充满悲壮肃穆感。仅以电影技巧而论，堪称年度最佳。'),(217,'罗马 Roma(2018)','阿暖','化腐朽为神奇的摄影，值得起立鼓掌的杰作，尤其是在这样青黄不接的一年。个人与大时代、叙事与抒情完美结合，摄影机移动的每一刻，既是冷静的审视，又是充满情感的凝望。Cleo有一双马的眼睛，如此温顺，但又永远悲伤。'),(218,'罗马 Roma(2018)','南悠一','可以借它探讨黑白影像的意义，电影铺就一幅“吾乡何处去”图景，其社会学价值与当下火热的非虚构文本相似，很私人，实际上仍旧不够独特。相比彩色，数字黑白更通透，也更能赋予它“观看”质感，更富有人文气质。它的艺术性得益于导演卓越技巧足够惊艳。女主角功能性太强，靠冲突推动被动改变，情感被忽视，卡隆还是有点技术纳粹。尤其他对社会结构的描写存理想化倾向，对“父亲”不敢面对，这一点不可被原谅。'),(219,'罗马 Roma(2018)','babydog不Muyan','1015极其令人失望的一厢情愿之作，像是想要拥抱大海，最后却抓的一手泡沫。Enprésenced’AlfonsoCuarón'),(220,'罗马 Roma(2018)','寒枝雀静','D＋/以为某种若即若离的视角提供了更为客观的观察方式，后来才发觉这只起到了抽空人物的效果。琐碎的散文式缀连始终没有铺陈出情感的暗流，到最后只和漫布全片的摇镜一样作空洞的凝望。而结尾更是以和《地心引力》同样的好莱坞方式抹掉了最后几丝残存的复杂性真实。仅能悦目而无法赏心的体验。'),(221,'罗马 Roma(2018)','CharlesChou','年度最失望——本以为预告片的情感震颤是冰山一角，谁曾想已经是张全景图。摄影和混响确实调动着全身感官，但故事始终锁死在一个孩子语焉不详的记忆视角里，从头到尾Cleo和母亲的角色都只是个演出卡隆内心自我感动的木偶罢了。为何如此和为何于此的问题，全都没有得到回答。'),(222,'大象席地而坐(2018)','东林君','有的人死了，他还活着'),(223,'大象席地而坐(2018)','Lycidas','有很多喜欢的地方，但更多的是遗憾。遗憾导演没有再抵御这个世界的严寒更久一些。若他活下去，可能就不会这么狭隘，不会把经验和常识不足当作外界加诸的苦难，把故作深沉当作青春姿态，也不会把父母和成年人都当作敌人。他再也没机会理解挣扎熬过去是什么感觉，是更苦涩，还是更宽广。'),(224,'大象席地而坐(2018)','PeterCat','结尾剧作和台词都露了馅，陷入一种偏执的无路可走，但这个死胡同其实是假的，是作者自己给自己按上去的，他当然知道出路或许是可能的，但他不相信，不相信事情和人是会变的，他更愿意相信那个死胡同，那个速朽的永恒。无论是电影还是真实生活。'),(225,'大象席地而坐(2018)','云隐MLI','一群猪飞上天的年代，不从事艺术行业的父母是新一代穷艺术家们的最大对立面和最终连带受害者。没办法单纯冷静地评价这部电影，全片看下来眼泪一直凝在鼻子里。斯人已逝，希望胡波父母能好过一些。'),(226,'大象席地而坐(2018)','咯咯精','你总是不高兴，跟个诗人似的。'),(227,'大象席地而坐(2018)','麦香鱼配?套餐','“你以为别人是傻逼看不出来你那要表达的肤浅的东西？？”'),(228,'大象席地而坐(2018)','岳漟','这个片子实在是……应该说，这个导演的心里实在是开不出一朵美丽的花。没有春天的人，拍出来的东西大约也是吻合他的偏执的。对于世界的认识与理解狭隘，模块化。就如三言，文字美妙，观点实在是男权封建。喜欢不起来。'),(229,'大象席地而坐(2018)','罌至','認認真真恭恭敬敬地看了完整版之後才有底氣說出我不喜歡這部片子。世界觀這麼狹隘偏激的人註定成不了大事。片中的三觀也真是200多邁的速度開歷史倒車，跟女德班不相上下了。一切都是你自找的。王小帥說的一點沒錯，“你以为别人是傻逼看不出来你那要表达的肤浅的东西？？”這四個小時我幹點什麼不好..'),(230,'大象席地而坐(2018)','陀螺凡达可','一股原始生猛的影像力量。'),(231,'大象席地而坐(2018)','水怪','华语电影的确很多年没有出现这样单纯展示愤怒与绝望情绪的电影了，这在很多过来人眼中的确还是一个稍显幼稚的表达。但对生活不满，纯粹骂街式的态度是青年人绝对必要的一个阶段。集体性理中客式的青年状态对于一个民族来讲甚至是恐怖的。'),(232,'大象席地而坐(2018)','法棍老人','别拍电影，别当作家，学个绘画，或者音乐，哪怕跳跳舞也行。——胡波。'),(233,'大象席地而坐(2018)','胤祥','#Berlin2018#友情加一星。大概是中国乃至世界范围内拍得最像贝拉·塔尔的（真的是学到影像逻辑的精髓了，音乐也是）用塔爷的方法讲了个一天之内的多线故事。影像风格比较像日本丧片。但是世间哪有这么多恨啊……还有的问题便是塔爷的风格和强戏剧性真的不搭，而且感悟式的说教也太多了点'),(234,'大象席地而坐(2018)','木卫二','去满洲里的人，不过是害怕坐在原地等老待死的生活无望。整片是又一出中国式家庭大型互害飙粗现场，狗逼狗屎废物泼妇瘪三恶心之类台词，接连不断，夹杂一些人生是痛苦的说教沉迷。长镜头设计相当用心，却像一把攥紧的双刃，刺向观众也刺中自己。一个成熟的导演，需要三部作品才能风格成型。我甚至想不到仅拍过一部电影就遗留在世上的电影导演。很遗憾，不需要评价胡波了。'),(235,'大象席地而坐(2018)','陆支羽','4.5。是啊，胡波并不是丧，是向死而生。无论是《井里的人》还是《大象席地而坐》，都是在狗日的绝望中寻找操蛋的希望，即便整个世界只剩下穷途末路的肮脏废墟，即便满洲里的大象只是空有嘶鸣的幻影。选择速朽，难道不是一种永恒吗？至少在那个出逃的夜晚，他们曾暂时放下愤怒，围在一起勇敢地踢毽子，宛如轮舞。'),(236,'大象席地而坐(2018)','凌睿','即便是张艺谋、冯小刚这样的导演，一旦创作受到资本的干涉就会严重影响影片质量。胡波的电影经历那么多风浪还能完成度很高，已经非常不错了。我们无法想象，假如制片人、监制多给胡波一些自由和空间，电影是不是会更好。可惜，我们永远也等不到那一天了。PS：11月17日，《大象席地而坐》获得金马奖最佳影片和最佳改编剧本，这应该是对当初不看好导演和这部电影的人们最有力的回击。'),(237,'大象席地而坐(2018)','邢早早','香港艺术中心的空调冷的我骨头缝疼，尽管如此，我还是睡过去了3次。对不起了导演，不好看就是不好看。我觉得自己这四个小时应该去爬太平山。'),(238,'大象席地而坐(2018)','次等水货','这个故事太真实了，就是我身边发生过或在我成长过程里看到过的，电影中的那些事件对峙、心理对峙，没有生活经验和内心思考过的人是做不出这样的电影的。大量的长镜头、跟拍，镜头虚实结合，在物理上它们不是景深镜头，可它们在叙事层面上却是极有深度的。导演原本有机会成为华语电影最有影响力的导演。'),(239,'大象席地而坐(2018)','LORENZO','悲怆的个人以及时代挽歌，致郁力百分之三百。四小时讲述的四个家庭盘根错节的愤怒与仇恨，对话极其冷酷，爱被深深埋进了故事线。音效渣，后期还有大量工作，但表演真是很赞，长镜头叙事也极具个人风格。你终于去了你魂牵梦绕的满洲里，一场电影结束了仿佛看完了你的一生。感谢胡波，R.I.P.'),(240,'大象席地而坐(2018)','桃桃淘电影','7，有很惊喜的地方，也有很遗憾的地方。但归根结底，它是不同的，胡波本身在文本上还是有基础的，有些镜头设计也很用心，配乐用得很出色。只可惜，这是唯一一部了……'),(241,'大象席地而坐(2018)','易老邪','国内文青拍出来的东西一言以蔽之就是——没头脑与不高兴，外加各种拧巴和死气沉沉。'),(242,'胖子行动队(2018)','桃桃淘电影','让人生理不适的东西，两人的胖子妆太恶心了，还各种拿胖开玩笑，各种卖蠢。问题是，你们的胖是化妆出来的啊！如果一个真胖子，他拿自己开玩笑，那叫自嘲。而装成胖子，拿胖子开玩笑，那叫人身攻击好吗？整部片子基本靠装傻卖蠢以及各种low咖笑话搞笑，文章的裤子被脱掉了起码5回吧，还玩了好几次屁梗。真是不恶心不会笑了，剧情本身傻得要命。真是快要把我看吐了。'),(243,'胖子行动队(2018)','我爱罗宾','好久没见过这么多新鲜的包袱了，还一个都不好笑，挺不容易的'),(244,'胖子行动队(2018)','后自愈','胖子这个设定除了能硬塞进一些笑点以外也实在找不到别的合理的理由了。徒手扒飞机模仿《碟中谍》，烟花爆炸及更衣室模仿《王牌特工》，没有出彩的点是自己原创的，而唯一主要的女性角色除了作为性暗示道具外没有别的用处。郭京飞继《21克拉》之后又演了一个结巴，这么喜欢拿结巴当喜剧点是有什么特殊的性癖吗？而这片居然还要拍续集才是最滑稽的地方好么！'),(245,'胖子行动队(2018)','大瘦瘦','主创似乎对胖子有着深深的误会，这就可以忽略掉那种偶尔出现的对结巴的恶意了。导演或许认为，胖子就应该受到歧视，他的另一半也就应该是个胖子或丑八怪或兼而有之（看早期克拉拉和结尾彩蛋）。克拉拉这个角色（甚至演员本身）已经沦为像柳岩一样的意淫工具，全片除了浇花带来yy外毫无用途（这种设置连个三角恋都没有，那你写文章和她的那几场戏除了满足男性观众视奸外还有何意义？）PS：你们支持这片的每一分票房都有可能成为它拍续集的资本'),(246,'胖子行动队(2018)','阿廖沙','如果全世界只能讨厌一个人！那一定是包贝尔！'),(247,'胖子行动队(2018)','搬砖侠','不出所料，国庆档最烂电影，甚至有资格入围年度十大烂片。故事太过于胡闹，剧本前后情节之间是完全断裂的，包贝尔的喜剧风格是最低级的耍贱卖萌玩下流梗式的LOW喜剧，以至于整部电影无论是人物设定还是故事情节都是胡闹且混乱的，致敬王牌特工的场景更是令人反感，就这样还敢在彩蛋中暗示有续集。对于聪明过了头的包贝尔来说，没天赋就不要自作聪明做导演了。另外，本片的唯一亮点可能是一头红发打女造型的张梦露了吧……'),(248,'胖子行动队(2018)','十一月份等等','包贝尔的观众缘之差都把文章洗白了'),(249,'胖子行动队(2018)','闷声看电影','戏都演不好，就做导演了？'),(250,'胖子行动队(2018)','华盛顿樱桃树','导演门槛快比演员低了。文章老师一如既往的装x。全程尴尬，欢笑有时。'),(251,'胖子行动队(2018)','吃胶片的考拉','王牌特工是做了什么孽，都被你们拿来抄了。'),(252,'胖子行动队(2018)','法兰西胶片','我原本没有像大家那么讨厌包贝尔，但这次一定是了。大概是100个短视频拼贴出来的电影，自以为讨喜了节奏，其实杂乱无章，层层断裂，这是对电影文本构架的无能无知。我也无法理解这个胖子特工的创意，角色始终架空在生活之外，特效化妆就像躲避这一切的安全胶囊，看上去导演对贾玲的了解都比对胖子的了解多，演好了我的功劳演砸了是胖子的问题，解构特工片也不一样非要肥胖啊，这是非常怯懦的做法，因为你对这一切类型化的东西都没有任何理解。其它什么抄袭、侮辱女性、人身缺陷歧视啥的，都和现在市面上所有这类烂片导演差不多，也就不多说了。最后想说一句，走到头还闹一个民族主义是干什么？你这是他妈有多自卑啊？'),(253,'胖子行动队(2018)','暖','充满了对胖子、对女性的深重恶意，品味极其低俗，克拉拉浇花配着MTV音乐的场景简直让人想直接离席，没有任何电影技法可言，只有充满意淫（还是极其低下的那种）的直男幻想，关于喜剧桥段的扮丑、卖傻、装疯、笑点也都尴尬到不行。'),(254,'胖子行动队(2018)','空山','文章说：好与不好都是导演的责任。'),(255,'胖子行动队(2018)','晚不安','国庆最差没毛病，吃力不讨好型的。带着一星的预期来看，一点都没失望。跟宝强的《大闹天竺》一样，属于没有金刚钻硬揽瓷器活。剧本创意团队那么多人整出这么个故事，所有陪包贝尔一条路走到黑的主创都挺可悲的。没有对比就没有伤害，麻花的《李茶》都显得熠熠生辉。用《王牌特工》的两段，我觉得不叫致敬就是挪用照搬啊……大部分笑点设计思路都是用高反差以及夸张的造型，万达影城的空调很冷，没想到电影更冷。映后比较震惊于文章的消极态度，看来对电影质量有很清醒的认识。'),(256,'胖子行动队(2018)','云南','娱乐圈简直是个神奇的地方。谁都可以演戏，谁都可以当导演。成了中国最没有专业性的两个专业。电影学院的表演系和导演系的师生真的要哭死，感情学不学都一样啊，毕业证真如同白纸。(￢_￢)'),(257,'胖子行动队(2018)','迪玛希','有的电影吧只能叫网大开片不到三分钟就闻出了烂片气息浓厚！'),(258,'胖子行动队(2018)','掉线','【C】居然不。是。烂。片。尤其是放在国产商业喜剧的语境下，甚至算得上中上。整体故事可看做肥胖版《你丫闭嘴》，还戏仿了《糊涂侦探》《盗梦空间》《王牌特工》《黑衣人》等一大批好莱坞大片。笑点足够，而且很多都是有设计过的包袱，（当然也有一些隔靴搔痒的尬点）不过后半部分崩盘有点严重。包贝尔品味不高，但还算有点追求，至少比开心麻花什么的像电影多了。'),(259,'胖子行动队(2018)','xslidian','适合东北口音爱好者的综艺公路片，对标《欢乐喜剧人》，笑点还没《影》多。TokyoBon完全被毁，两遍都不放完整，歌词错，画面不搭。大量出现中文字型的日语，让人怀疑是山寨版的日本。医院可能叫中日友好医院，路标全中文。除肖进外是不是没有化妆团队啦，胖子的手和脸不是这样的呀。'),(260,'胖子行动队(2018)','凌睿','今年国庆档《找到你》和《胖子行动队》可以连着看，一边马伊琍为了家庭可以放弃器官、出卖身体、从事最卑微的职业，甚至怀孕的时候还要挺着大肚子去打工，而他的丈夫家暴、赌博、强奸，干尽天下坏事，甚至女儿生病了也不管；另一边，文章却见一个爱一个，见到每一个女人都要说什么“心悦君兮，君不知”和“我喜欢你”，最后他还说这都是假的，只是为了完成任务。'),(261,'胖子行动队(2018)','阿日炎','神特么特工J，你已经被锁定惹'),(262,'一个小忙 A Simple Favor(2018)','女王人妻','真是一部婊气冲天的电影.brotherfucker和sisterkiller的巅峰对决.不要在这片里讲三观，我只想找Blakelively做我的老公.里面几套衣服让我弯得全程流口水.'),(263,'一个小忙 A Simple Favor(2018)','无语邻','AnnaKendrick终于得到一个可以尽情发挥演技的复杂角色，也确实演得好，大半部戏独挑大梁，发挥到发光发热发辐射。但是到了BlakeLively还魂那场，女王样上身真空、敞怀穿条纹西装外套，打一条短领带，不必说话，就那么一站，Kendrick之前半部电影的发挥立刻变了灰化肥挥发化作灰灰。'),(264,'一个小忙 A Simple Favor(2018)','Lacie','她俩kiss那里我以为结局会是男主被设计进监狱，她俩拿着400万带着两儿子远走高飞了。。。'),(265,'一个小忙 A Simple Favor(2018)','派翠克','这片最大优点是明明是悬疑惊悚却偏偏拍得很喜剧的感觉，AK这次演了个又nerd又神经质又受又绿茶的妹纸，如果真的拍成了姬片结尾那多好'),(266,'一个小忙 A Simple Favor(2018)','米粒','GoneGirl之《我比你更婊》…'),(267,'一个小忙 A Simple Favor(2018)','AilsonAir','可以说比想象中的好。悬疑部分虽略显凌乱但总比千篇一律地移植老套路强。而且这片子婊里婊气的冷笑点真是一个接着一个。我是觉得，Anna这种有点nerdy的心机圣母白莲花和Blake这种高端美艳女王范儿的毒妇还真是蛮有化学效应的。虽然管不住下半身的男人都是混蛋，但莫名觉得他一个青铜被夹在两个王者玩家中间有点可怜?'),(268,'一个小忙 A Simple Favor(2018)','Freia','傻逼电影我看完真是一肚子火真是大bitch到无法无天teen就睡了自己哥哥然后睡别人男人在别人家录傻逼做饭vlog还抱着别人的儿子然后一脸goodmom最后还了不起有1M粉丝哇！'),(269,'一个小忙 A Simple Favor(2018)','toishiki','BL那么骚那么帅那么香，anna你到底为什么！！！！！不搞姬搞到底！！！不开心！！！'),(270,'一个小忙 A Simple Favor(2018)','SleepWalker','BrotherfuckervsSisterkiller.婊气冲天版《消失的爱人》，安娜肯德里克婊现太强大了，没有不尊重人，表现确实太好了，压了气场强盛的蕾弗利一头。得益于演员精彩表演和还算花了心思的剧本，这是那种全程都不会冷场的片。最后的连续反转戏剧性过强太浮夸了，结局也有很多种走向可以选，但导演还是选择了政治最正确的那种，这个故事的腹黑程度也打了折扣。'),(271,'一个小忙 A Simple Favor(2018)','托纳里诺阿欧','为什么不搞姬？？？？？为什么不搞姬？'),(272,'一个小忙 A Simple Favor(2018)','李阿斗','这么多年过去了，我还是愿意给QueenS提鞋！！！'),(274,'一个小忙 A Simple Favor(2018)','陶者无缰','真空套裝的Emily也太霸氣了吧，亞洲壕十分精英的樣子很帥，結果是個大豬蹄子。'),(275,'一个小忙 A Simple Favor(2018)','张小思','S的吻戏真是带感，不管对方是男是女都如此。笑点不老套，剧情虽然经不起仔细推敲，但还算有趣。'),(276,'一个小忙 A Simple Favor(2018)','强力灵魂大重猫','好笑。美国混动，无声更致命！'),(277,'一个小忙 A Simple Favor(2018)','有趣','S女王完美驾驭霓裳羽衣和婊气十足'),(278,'一个小忙 A Simple Favor(2018)','西楼尘','占了鸠巢的鹊，狠心剪开不合身的长裙；借尸还魂的鸠，忍痛抛扔半空中的扳手。女人的战争，不仅心得狠，还得不怕痛。在葬礼上无视伦理与哥哥暧昧，在母胎里哪管亲缘将妹妹谋杀，在播客前彷如无公害的主妇，在镜头里装作受家暴的人妻。男人沦为抓住下体任意嫁祸，打伤锁骨无可奈何，沉溺危险无用的玩物。'),(279,'一个小忙 A Simple Favor(2018)','桃桃淘电影','整个故事发展都非常曲折离奇和丰满，虽然没那么有趣，但是亮点还是挺多的，包括有些许反类型的设置。两个女演员都非常出色。'),(280,'一个小忙 A Simple Favor(2018)','闪电标','以后真的要每年去一次新西兰看电影了，比美国都早一天，whynot？\r这俩一个比一个姬，摘金奇缘的男主出演同夫，可谓苦不堪言…'),(281,'一个小忙 A Simple Favor(2018)','柯里昂','不恨腿短，只恨天高。——安娜·肯德里克本部戏的心理独白。'),(282,'巴斯特·斯克鲁格斯的歌谣 The Ballad of Buster Scruggs(2018)','凌睿','一顿操作猛如虎，结果是个二百五。阎王要你三更死，多活两秒也要死。虎落平阳被犬欺，落魄凤凰不如鸡。杀人之后不补枪，绝对死得硬邦邦。遇到危险别冲动，否则后悔也没用。人生就像坐马车，该下车时就下车。'),(283,'巴斯特·斯克鲁格斯的歌谣 The Ballad of Buster Scruggs(2018)','kylegun','把酒屠馆空手爆头快枪歌神升天记，锅男红番吊颈秋千红颜一笑看得开，话语形体皆物化之讲故佬终遭计算鸡更替，TomWaits桃花源淘金独角戏命硬江湖，开荒车队日常浪漫难敌土拨鼠生死修罗场，人分几种情有多深阎王面前皆虚妄……开心残忍浪漫诗意，科恩兄弟元素齐活，好评'),(284,'巴斯特·斯克鲁格斯的歌谣 The Ballad of Buster Scruggs(2018)','LORENZO','典型科恩黑色寓言，西部的部分还有昆汀的感觉。最喜欢付兰兰的那个，最短最好笑。最悲伤的，应该是连姆尼森那个，特别绝望。虽然很棒但不如罗马。'),(285,'巴斯特·斯克鲁格斯的歌谣 The Ballad of Buster Scruggs(2018)','二十二岛主','威尼斯第9场主竞赛。目前观感最佳的一部，充满了科恩兄弟的奇思妙想，每一个故事都足够荒诞且意味隽永，尤其是连姆尼森的那个故事拍的最有风格。其实并没有太大的野心，只是想把这几个故事讲好，开头两段颇有昆汀风格的故事把整个影厅的人都看高潮了，同场还有胖陀螺和评委们，不知道他们是否喜欢。'),(286,'巴斯特·斯克鲁格斯的歌谣 The Ballad of Buster Scruggs(2018)','亵渎电影','科恩兄弟给Netflix拍的网大，正确的打开方式应该是当美剧看吧，6集美剧，每集的风格都不一样，都是美国西部的老故事，所以叫诗选集。兄弟俩这回很随意，美学风格发挥到了极致，做剧集也更放飞自我，不想写电影剧本那样紧凑，到了Netflix手里还能做自己的导演都很强。最爱佐伊·卡赞那集，一度非常的浪漫，没想到结果却是这样的，可能这就是当年的美国西部吧，就知道在科恩兄弟的世界里不存在什么浪漫，一直就是荒诞和黑暗。'),(287,'巴斯特·斯克鲁格斯的歌谣 The Ballad of Buster Scruggs(2018)','竺.不二.哼哼','可视为章回体，改名《米国西域恩仇录》，分为六回。第一回：此曲只应天上有,言多必失终有尽。第二回：零落岁月莫蹉跎,不抢银行不放牛。第三回：粉身碎骨全不顾，要留丧感在人间。第四回：千淘万漉虽辛苦,少吃鸟蛋始到金。第五回：曾经沧海难为水，该出手时就出手。第六回：词穷。'),(288,'巴斯特·斯克鲁格斯的歌谣 The Ballad of Buster Scruggs(2018)','欢乐分裂','西部片集锦，风景壮阔苍茫雄浑，以古典的讲故事方式串联六个富有隐喻的故事，表象不无搞怪、古怪，其实依旧充斥着科恩兄弟式的悲凉宿命感，无论怎样抗争扑腾，最终难逃死神之手的翻云覆雨。死亡被处理得迅疾、简单、莫名甚至轻盈，最后一个故事貌似无稽，其实是对之前五个故事的总结——任你再留恋回望，到头来我们都将走向死神的宫殿。'),(289,'巴斯特·斯克鲁格斯的歌谣 The Ballad of Buster Scruggs(2018)','陀螺凡达可','3.5前四个都不错，从荒诞歌舞喜剧到黑色幽默再突然变调成悲歌和咏叹调。最后两个稍微差点。'),(290,'巴斯特·斯克鲁格斯的歌谣 The Ballad of Buster Scruggs(2018)','沙雕电影','《慢调西部》教会了我晾衣绳的妙用；《大地惊雷》教会了我如何讨价还价；《被解放的姜戈》教会了我黑人的头骨有三个凹槽；《战斧骨》教会了我印第安人肉料理的烹饪技法；《荒野大镖客》教会了我如何打德州；以上的所有作品教会了我如何欣赏《巴斯特·斯克鲁格斯的歌谣》★★★★★'),(291,'巴斯特·斯克鲁格斯的歌谣 The Ballad of Buster Scruggs(2018)','大海里的针','六个故事，结构分散却风格统一，融合了《大地惊雷》的狂野西部，《醉乡民谣》的忧伤旋律，《老无所依》的世事无常。'),(292,'巴斯特·斯克鲁格斯的歌谣 The Ballad of Buster Scruggs(2018)','内陆飞鱼','科恩兄弟拍西部片总有一手，这部依然娴熟老练，节奏，剪辑，摄影，全部在线，很好看，每个故事的聚焦点不一样，有艰辛，苦涩，孤独，向往，荒诞，奇情，惊悚，黑色，最悲伤的是TheGalWhoGotRattled，当老牛仔打退印第安人，回到土堆后面，女孩已在恐惧中开枪自杀，刚刚露出微茫的幸福一瞬即逝，一种无法克制的悲伤弥漫开，人命啊，在荒诞与悲凉中克己求生，有时就是脆弱如风中的芦苇，咔嚓一下就折断了。'),(293,'巴斯特·斯克鲁格斯的歌谣 The Ballad of Buster Scruggs(2018)','南悠一','西部片开场但打破套路，反思和情怀并存，整部电影依然是个华丽丽的歌单，味道还是科恩兄弟。六个寓言故事，轻巧简单，带给我浑厚的悲伤，第六个看着不好，但实为终结之笔，第五个将整部电影的反思于当下的语境里建立起来，故每个都是人性的浓缩胶囊，每个都让我喜欢不已。威尼斯私人最爱的两部之一。'),(294,'巴斯特·斯克鲁格斯的歌谣 The Ballad of Buster Scruggs(2018)','汽车大师','老美国志异，护镖那个简直是多年所见最别致的浪漫爱情片，尤其在刚看过摘金奇缘后'),(295,'巴斯特·斯克鲁格斯的歌谣 The Ballad of Buster Scruggs(2018)','室内滂沱','《六恶人》。。每个小故事都是在卖萌的外表下，一点点渗透出特别残酷的东西，是六个“怎样启动创意”的范文，如何在短时间内交代人物个性、做剧情转折、唤起观众认同感、巧妙收尾，两个多小时里能学六次，非常值了。六个故事风格不一，但要说的事大致类似：在茫茫天地间，人如何与自己的处境共处。套用荒野西部片的壳子，景观引发视觉美，特定时代赋予人物特定特征，用一本书强调故事的虚构性，增加文本意义的同时能让叙事更从容，转折太夸张也没关系（因为是书里写的啊，咱们看的是一本书呢），典型的欣赏“写法”为主，看故事为辅的电影。如何把明星演员无痕置入其中，也非常典范。“值得学”大于“很好看”，虽然每一段都有点意犹未尽，而且事后回想，故事的创作痕迹都太过明显，但本着学习技巧的目的，经常拿出来看是没问题的。4.5'),(296,'巴斯特·斯克鲁格斯的歌谣 The Ballad of Buster Scruggs(2018)','桃桃淘电影','科恩兄弟的西部故事集锦，无论在美学、风格以及剧情元素上，都有很多的变化，有荒诞、黑色幽默、也有伤感抒情的。而从剧情上，也融合了决斗、抢劫、淘金、开拓、与印第安对战等经典西部元素。不过，因为是6个集锦故事，也使得影片少了长片在情感上的完整，更像是科恩兄弟玩票的东西。最爱弗兰科和佐伊·卡赞两段。'),(297,'巴斯特·斯克鲁格斯的歌谣 The Ballad of Buster Scruggs(2018)','罗夏','太有意思了。电影看出了短篇小说的语感，如同欧亨利的小说一般，六个故事，各有趣味。就电影的娱乐性来说，科恩兄弟绝对是其中翘楚。就电影感来说，每一祯画面都很电影，各种镜头的切换，构图的选择，对于观众来说，是一次极致的享受。就表演来说，每一个角色都足够丰满，并且满脸的故事与演技。每一段故事采取不同的用色，每一段都风格迥异，有无厘头有幽默有悲伤有浪漫，最后一个故事甚至有着独特的嵌套美感。镜头结束在书籍合上的瞬间，故事完了，余味无穷，同样完成了一次嵌套的艺术。'),(298,'巴斯特·斯克鲁格斯的歌谣 The Ballad of Buster Scruggs(2018)','CharlesChou','除了2与3，并不比HailCaesar翻身多少'),(299,'巴斯特·斯克鲁格斯的歌谣 The Ballad of Buster Scruggs(2018)','同志亦凡人中文站','美国西部的蛮荒故事，生死转换有如轮盘赌。虽然没什么深刻意义，但通篇的黑色风格很科恩兄弟。PS果然越是短小的故事越考验编剧啊~~~~~'),(300,'巴斯特·斯克鲁格斯的歌谣 The Ballad of Buster Scruggs(2018)','安东','所以，都得死。'),(301,'巴斯特·斯克鲁格斯的歌谣 The Ballad of Buster Scruggs(2018)','康报虹','六个风格迥异的西部故事，或轻松，或荒诞，或绝望，或诡谲，看似毫无关联，实则都关乎“死亡”。《巴斯特的歌谣》中的巴斯特·斯克鲁斯身上有许多经典西部英雄的影子，科恩兄弟还为这个角色注入了一些卡通色彩，让他时不时打破第四面墙与观众对话，整个故事轻松而讽刺。《阿尔戈多内斯附近》短小精悍，荒诞意味十足，显然死神不会每次都被他欺骗。《饭票》是我最喜欢的一个故事，那只鸡真的会算数吗？我希望不。《黄金谷》有点像西部版的《老人与海》，但与老人抗争的不是大自然，而是他的贪婪和欲望的眼睛。《受惊女子》有着一个悲剧结尾，如宿命般不可抵抗，如上帝般戏弄世人。《遗体》是对前五个死亡故事的总结，与《幽灵马车》的气质相和，整个故事就是一个巨大的隐喻集合体，用不停歇的马车预示人通往死亡的过程，无处可逃。'),(302,'名侦探柯南：零的执行人 名探偵コナン ゼロの執行人(2018)','施小音爱林先生','特大喜讯，特大喜讯，安室还单身！'),(303,'名侦探柯南：零的执行人 名探偵コナン ゼロの執行人(2018)','ぐだぐだ','你们安室透握个方向盘都能把我给笑疯了，后面说自己的恋人是国家我简直笑到要跳楼，真是社会你动作大片'),(304,'名侦探柯南：零的执行人 名探偵コナン ゼロの執行人(2018)','123','安室透基本没啥戏份唯一的“执行”就是那波儿反物理开车让我觉得他当公安前家里是不是卖豆腐的头文字D都不敢这么编没有什么是180迈解决不了的如果有就再踩一脚油门儿总之欢迎大家收看头文字柯南之看门狗2'),(305,'名侦探柯南：零的执行人 名探偵コナン ゼロの執行人(2018)','冰岛好人','槽点太多不知道从哪里开始说，以前柯南当滑板侠稍微违背下牛顿力学也就算了，这部一个普通日本公安居然能轻易破解NASA密码绑架卫星；用足球对冲卫星而且产生的爆炸几乎没有…而且公安的协助者这个设定也太牵强了吧，总而言之这部剧场版小孩子看不懂，成年人看了侮辱智商，无推理成分，无恋爱要素，最后安室透那句“我的恋人是这个国家”，这日本战狼式的发言是怎么回事？！'),(306,'名侦探柯南：零的执行人 名探偵コナン ゼロの執行人(2018)','Lain','今年简直是堪称“看到了柯南剧场版转机”的精彩！节奏和剧情超棒，快要成安室的脑残粉了。虽然福山雅治的歌不是很搭，但是超级期待明年的基德×新加坡好吗！btw，帮安室设计“我的恋人是国家“这种台词是怎么想的…'),(307,'名侦探柯南：零的执行人 名探偵コナン ゼロの執行人(2018)','雨知时节','3.5星。真的一年比一年更像动作片，另外，在犯人告白那段我居然看出了基情......'),(308,'名侦探柯南：零的执行人 名探偵コナン ゼロの執行人(2018)','Malloy','2.5“我的恋人，就是这个国家啊！”———————好久没有听到这么烂的话了'),(309,'名侦探柯南：零的执行人 名探偵コナン ゼロの執行人(2018)','Richthofen','下面有请关东著名杂技演员给大家带来家国思想价值观输出'),(310,'名侦探柯南：零的执行人 名探偵コナン ゼロの執行人(2018)','RockJ','反正你拍的再烂我也会去看的。'),(311,'名侦探柯南：零的执行人 名探偵コナン ゼロの執行人(2018)','是苏打的小仙女','柯南问安室你有女朋友吗，安室回答我的爱人是国家的时候。。东南亚电影院的观众们同时发出了杠铃般的笑声'),(312,'名侦探柯南：零的执行人 名探偵コナン ゼロの執行人(2018)','夜神月的猫','犯案动机太狗血，狗咬狗一嘴毛，手机能控制卫星，精准如弹道导弹，足球成拦截导弹。日本政府真的不再需要什么低级的路基宙斯盾、爱国者3、标准3等防空系统了，柯南基本承包了日本的自卫防空力量。全片简直是极限特工3的片头+碟中谍4阻止导弹坠地+各种阿汤哥都不敢玩的特技，这还是什么侦探片么？？'),(313,'名侦探柯南：零的执行人 名探偵コナン ゼロの執行人(2018)','花開院弥生','看到最后一直在想真正的主角【皮球】怎么还不登场，结果果然没有让我失望。哦对，主役是皮球的话，二番就是安室的车了吧。'),(314,'名侦探柯南：零的执行人 名探偵コナン ゼロの執行人(2018)','科林','画面如此粗糙反而票房最高…撅倒…都已经这样了，毛利兰还没猜到柯南就是新一……这一个~美丽的~美丽的笨女人~Oh~~~'),(315,'名侦探柯南：零的执行人 名探偵コナン ゼロの執行人(2018)','小绵羊','前面的小男孩突然问起诉是什么意思，我就在想这个故事小孩看得懂吗？动作场景依然需要帮忙按住牛顿的棺材板。'),(316,'名侦探柯南：零的执行人 名探偵コナン ゼロの執行人(2018)','翰小七','拍成动作片也就罢了！连柯南的业务能力都下降了！竟然问出安室有没有女朋友这种问题！帮帮忙好伐！安室是有男朋友的人啊！标题还得怎么暗示啊！拙计！'),(317,'名侦探柯南：零的执行人 名探偵コナン ゼロの執行人(2018)','蓝色之心','柯南还当什么侦探，直接加入复仇者联盟好了'),(318,'名侦探柯南：零的执行人 名探偵コナン ゼロの執行人(2018)','家住赫宰市','安室透的车技也是在夏威夷学的吧，重现纽约的辉煌了/抱拳有一幕真的让我怀疑是在看你的名字了哈哈哈哈哈哈，有了这个想法听主题曲都觉得像前前前世柯南真的是见证了20年科技的变迁了?下一部剧场版再一次基德OKOK'),(319,'名侦探柯南：零的执行人 名探偵コナン ゼロの執行人(2018)','徐若风','难看到这份上也是不容易，不过车失控和收尾这两段动作戏还是有点料的。近五六部的剧场版格局都铺的有些太大了，无论如何，柯南现在都已经回不去本格推理了。'),(320,'名侦探柯南：零的执行人 名探偵コナン ゼロの執行人(2018)','judylovesanime','7/10在台湾看的补评，我觉得国内的一些观众需要看清楚一个事实，蜗壳的剧场版向来就是以故事的悬疑性和动作戏码作为卖点的，跟传统的推理模式真的沾不上什么边，要求主创们认真地像TV的模式一样讲推理真的不是很现实。今年的剧场版虽然有不可避免的合理性失衡和动机上的硬伤存在，但从演出角度来看细小的调度和气氛的躁动感较历年水平有所提升。另外就是剧本的成分的加大，对本片最为核心的理念“正义没有唯一”做了深入。动作戏的篇幅其实只占了十分钟不到，且能迅速地为下一场戏作铺垫，比起静野时期过分展现奇观而从根本上忽略了叙事的紧凑性和完整性的做法无疑是可取不少。总之今年的M22还是有许多值得认可的亮点，希望立川导演的心之处不会因为路人粉群体对近年剧场版的排斥心理而被忽视'),(321,'名侦探柯南：零的执行人 名探偵コナン ゼロの執行人(2018)','manubei','连情怀都没有了的柯南…全片一个亮点也没有…全程尿点+睡点…有0星就好了…跟了柯南二十多年…电影院不会再去了。这部太伤人感情了。'),(322,'宝贝儿(2018)','罗洛','我以为杨幂年过30演技终于开窍了，没想到预告就是巅峰……。杨幂的情绪处理和表情控制能力，差得不能想象，台词功底依旧没能摆脱气虚无力，过于尖锐的缺点。尤其是在和一众实力派对比之下，简直是行刑现场，这样的电影很值得社会关注，剧情比较完整，但是这个演技依旧不过关'),(323,'宝贝儿(2018)','玉露哥哥','这部电影看完以后很难让人不想到多年前的天津无肛女婴事件，如果是在这件事的基础上做的改编，杨幂演的就是陈岚这个吃人血馒头的渣滓，并在原事件的基础上对陈岚女士的形象进行了美化。冲着这一点就根本不想给高分。在微博上看到的杨幂郭京飞冲突那一场戏竟然是全片的高潮，杨幂女士口口声声的转型之作怕不是个笑话吧？刘杰导演声称的九年磨一剑，怕是以毕志飞为榜样的吧。如果豆瓣有零分，我会给零分，这一星是给男演员的。'),(324,'宝贝儿(2018)','毛小惺','好怕被人骂，但剧本真的很粗糙，作为流量明星，素颜出演很拼，但演技尬到不行！'),(325,'宝贝儿(2018)','嘎嘎','杨幂很努力了，支持'),(326,'宝贝儿(2018)','流年','杨幂的演技真是从来没让失望过，一如既往的棒棒啊。全影评片的高潮就是杨幂和郭京飞争执的那段，呵呵，就没了。号称磨了九年的佳作啊，这次导演被坑了'),(327,'宝贝儿(2018)','NuoMiCi','刚好人在加拿大有幸作为第一批观众看了首映。第一次见杨幂素面朝天出现在荧幕前讲述普通人不甚至可以说社会底层人民的故事。但她似乎又有些不同自己受到了不公的待遇不甘屈服甚至能够为了不让素不相识的孩子也受到这样的待遇去奔走。有些意外的是故事结局不是像大多数故事一样女主光环你努力了就有结果。我想导演想表达的大概是这就是现实。'),(328,'宝贝儿(2018)','豆友184502156','本以为杨幂所谓转型之作能给我带来对她演技的改观。但是让人很失望的是，她似乎还是沉浸在自己程序化的表演中，对情绪的把握都体现不出人物的立体性。男主的演技还不错，但是更衬托出女主对人物诠释不足。'),(329,'宝贝儿(2018)','Hain','证明演技不需要扮丑，利用外形博好感和饰演大女主戏本质上没有区别'),(330,'宝贝儿(2018)','BAzINGA','号称是“杨幂的转型之作”，扮土扮丑就叫转型了？麻烦focus演技。台词超级差，全片都没什么表情。剧本也很差，叙事性为0，让人昏昏欲睡。还有，真的很想抽江萌一嘴巴子，超级圣母婊。'),(331,'宝贝儿(2018)','孟三哥','女主表现全无层次感，浮在人物表面，方言根本谈不上。郭京飞亦比想象中平淡，不过台词好过女主太多。口碑不是单靠炒作发通稿就行的，要靠真质量好的作品，观众自然认可，一直演烂片，想回血口碑，也得多演几部合格的戏，表现一下诚意，突然扮个丑就到处发通稿演技翻身就想一次洗清，难度太大扯着蛋'),(332,'宝贝儿(2018)','王大根','我不知道换一个人演能不能让我产生更多的共情，但杨幂只能让我感到“这个女人真的好烦”。杨幂完全让我患上了电影院抽动秽语综合症——因电影的漫长而不耐烦到浑身抖动，演员的垃圾演技又让我不受控制地开始说脏话。一个满脸就写着“我看不起你们这些穷人”的人，到底能怎么演底层？她就连拖地和洗碗的样子都明明白白地展现着“这是我有生以来第一次拖地和洗碗”。如果说把脸涂黑再变换一个拙劣口音就算演技突破了的话，那我写东西时换个字体颜色是不是也等于文学造诣突飞猛进了？Anyway李鸿其还是很帅的，聋哑属性加成后更帅了，杨幂不要你我可以'),(333,'宝贝儿(2018)','啦波塔的水行者','本人有幸在多伦多看过12号的首映，也有幸见到了电影的主创。良心话这部电影真的是很优秀的作品。故事主角江萌因身体缺陷问题从小被亲生父母遗弃，所以当她看到另一个与她有着同样命运的孩子时毫不犹豫的选择去救她。电影看着似乎很平淡，但是后劲很大。刘杰导演真的是一个很有个人风格的导演，说实话这个题材我真的以为温情路线可能更加吸引人，但是他却选择了纪实风格。真的很敢啊！再来说一下这部电影的绝对女主角杨幂，说实话我一直认为大众对她有误解，她的演技真的被低估或者忽略了。这部电影让我重新看到了她的可能性。希望以后她能多拍一下不一样风格的电影。'),(334,'宝贝儿(2018)','太阳当_空_照','tiff上真人比电影里更不走心'),(335,'宝贝儿(2018)','Z星驰007','杨幂总是能把苦情戏咆哮出“大家快看！我在努力表演”的祈求感。'),(336,'宝贝儿(2018)','醉鱼','多伦多首映打卡！整个电影节奏有些散，冲突和人物塑造并不完美。但是杨幂首次尝试这种类型的影片，我觉得是及格的。有感触的一点是，Q&A环节杨幂讲到，我们并不能以一个上帝视角去评判他人的喜怒哀乐，而认为别人可怜。虽然这是一部讲残疾人的电影，但这点拿捏的很好，并没有故意渲染苦难。'),(337,'宝贝儿(2018)','此致终年','导演想要表达的东西太多，却又重点不是很明显（突出）。演员演技ok，就是剪辑有点乱。怎么说呢，代入感挺好，杨幂也挺有勇气的，演得挺棒的，台词可能稍微不那么好，可是过关。本来想三星的，可是尬黑的人太多，李鸿其饰演的角色会说话，台词吊打杨幂？我觉得挺好的，不好意思，我认为的好，就是能带动我的情绪让我关注她的心理，她的故事。我觉得这次杨幂很ok。因为对一个女明星的厌恶，竟然可以给为残疾人说话的电影泼这样的脏水，有的人怎么能恶到这种程度？本来想四星的，不好意思，看不下去尬黑的'),(338,'宝贝儿(2018)','何小沁','社会现实题材、人性拷问，重度先天残疾的弃婴该不该救，婴儿or老人没有意识的情况下究竟谁有权利决定他的生死，都是世界各国讨论了很多年的话题。这部还聚焦了中国特殊年代产物--寄养儿童（非收养儿童）群体的成长和心理，展现了ZF、警方、寄养双方间的一些遗留问题（养老、遗产）。刘杰导演依旧叙事流畅手法克制，对演员的调教也很厉害。李鸿其太好了，真是好演员，聋哑人演绎很真实，也避免了台湾腔的问题；杨幂突破形象，演技有所提升，南京话虽然不是100%标准但也很下功夫了'),(339,'宝贝儿(2018)','大卫好奇','这个杨幂，是你从未见过的全新版本。本片作为2018年在「我不是药神」之后的又一部现实题材国产片，值得被更多人关注和了解。导演刘杰说自己并未给影片预设立场，当观众看到影片结尾，究竟是认同杨幂饰演的女主对于救孩子的执拗，还是理解郭京飞饰演的父亲放弃治疗的决定，都取决于观众自己的决定与思考，影片只是把这种复杂又实际存在是人性难题用纪实的手法浓缩展现。有时候。面对这样的现实困境，提出问题和提供视角，比给出一个创作者的主观答案更重要，也比站在自己的道德观进行说教更有力。'),(340,'宝贝儿(2018)','CareyHao','TIFF首映。整部电影就是一部感动了自己恶心了别人的电影。人家父母因为不愿意让孩子受那个苦，医生说孩子也有可能支撑不下来，你就去偷医院档案打电话疯狂骚扰人家，跑到人家家里骚扰人家，然后就偷孩子了？杨幂的演技的确进步了，但是一半方言一半普通话有意思吗？'),(341,'宝贝儿(2018)','我爱罗宾','从杨幂的角色就可以看出这是一部非常克制的电影。从头至尾导演都没有站在上帝视角去说教而是选择给观众思考空间，生与死的命题潜移默化地走进观众的思想。留白够体贴，同时题材也很值得关注，这正是当前国产电影最需要的'),(342,'此房是我造 The House That Jack Built(2018)','树洞','你别说，就算拉丝的电影里面只有一个屁股对准镜头120分钟不动还是有很多人会打五星'),(343,'此房是我造 The House That Jack Built(2018)','胤祥','#Cannes71#完全没想到拉斯冯提尔拍了半部对话体论文电影！这用电影史和艺术史的水平比斯派克李高到不知道哪里去了！他就是用这部片子解释上次戛纳那句支持纳粹的言论，反正说清楚了。另外此片必然与内地大银幕无缘，别想了……就是自恋程度和自说自话程度也很高，节制一些会更好'),(344,'此房是我造 The House That Jack Built(2018)','LORENZO','要么名垂青史要么遗臭万年，拉斯冯提尔又要炸裂了！作为粉丝只想说又爽又难受，不少镜头简直......和<<女性瘾者>>一样是对话电影，还闪回了之前他的所有作品，和贾樟柯一样自我致敬?。但是缺少深度，明显不如<<忧郁症>>和<<女性瘾者>>等的立意，撒旦地狱镰刀太直白了。'),(345,'此房是我造 The House That Jack Built(2018)','十二辰子','本届戛纳最爱大量哲学对话绘画作品的融合聚焦在一个极端残酷的变态杀手内心深处的精神与思想没有挣扎只有艺术的理所当然结尾但丁与维吉尔共渡冥河负片中光源是最黑暗的地方他最终抵达了这个最明亮却最黑暗的地狱太惊喜了'),(346,'此房是我造 The House That Jack Built(2018)','圆首的秘书','我现在很理解为什么有人会离场，因为这个片子确实让人如坐针毡，但别的地方都黑着，只有银幕是亮的，你不想看不想听也无处可躲，只能选择出逃。但与此同时，这又是LvT最正的一部电影（结局），他通过提出一些美学和艺术层面的问题，让你的价值和灵魂发生震颤，从而产生真正的哲学意义，而非政治正确。他讽刺纳粹病态美，指责美式虚伪，还要打脸戛纳：他要冲击的就是一个连讨论都不让讨论的禁区。以真正艺术家的方式回到了戛纳，哪怕是非竞赛单元，无疑是LvT的一大胜利，更是艺术的一大胜利。'),(347,'此房是我造 The House That Jack Built(2018)','九只苍蝇撞墙','所有拉斯冯提尔的片里我最喜欢的一部。此前从《乘风破浪》开始，他就习惯性地把主角伪装成被无端迫害的受难者，以为各种纳粹屠杀式反击提供充足的借口。只有这次他一改虚伪的作派，让人物直接以变态杀人狂亮相，邪恶到了萌动可爱的地步，同时将毁灭提高到艺术的层面，过足了挑战人类道德底限和毁灭异类的瘾头。拍片治病是拉斯日常的生活方式，但不能否认的是他创造单场惊悚戏剧性张力的超强天赋在本片里发挥地淋漓尽致，将观看神经时刻紧绷到了极点。这是不是对法西斯式的极端受虐/施虐情绪的一种肆无忌惮的张扬炫耀？我更愿意将它看作是一面镜子，感到不适很可能是因为观看者在其中隐隐观察到了自己背离伦常的潜意识欲望。'),(348,'此房是我造 The House That Jack Built(2018)','二十二岛主','戛纳第18场特别放映。恭喜冯提尔为影史贡献了又一位迷人的杀手，也为我带来了在戛纳最为酣畅的观影体验。从本质上说这并非电影，而是艺术品，冯提尔将他对影像的疯魔与人物融为一体，当杀戮成为建造的过程，杀戮本身不再重要，当中还涉及到冯提尔对艺术政治以及自己作品的解读，当之无愧的里程碑之作'),(349,'此房是我造 The House That Jack Built(2018)','Tsùn-hông','Seriouslyyouguyslovethisfilm?'),(350,'此房是我造 The House That Jack Built(2018)','昨夜星光','拉斯·冯·提尔的新片《此房是我造》在戛纳引发了强烈的争议。在周一晚上的放映中途，有超过100人中途离场，不少人认为这部电影“过于让人恶心”。“提前退场！射杀孩子的画面绝不是艺术，也不是娱乐。”'),(351,'此房是我造 The House That Jack Built(2018)','red.','残杀画面太冷酷会有激励杀手和sociopathe的作用，这绝对是事实。LVT想说自己是艺术家要解放艺术。而实际上他不过是个病人用拍电影来治病。他用这部电影把这些年各路媒体骂他的话都洗白了一遍还不忘记带上电影的画面。古尔德很无辜和那么多血腥的画面在一起以后是忘不掉了。其实最受不了的是他的自恋。'),(352,'此房是我造 The House That Jack Built(2018)','牛腩羊耳朵','拉斯冯提尔不疯也不魔，反之同情心却泛滥成灾到了“道德反面”。这部让冯提尔重回戛纳的艺术史论文与连环杀手速写，就是他对七年前被驱逐出戛纳的回答，冯提尔让因饰演希特勒而闻名的BrunoGanz成了Jack的先知。金黄色的镰刀映衬着的恬静被工业文明的轰鸣搅得一片浑浊。'),(353,'此房是我造 The House That Jack Built(2018)','王写写','道格玛95给他最大的遗产就是摄影，而在经历欧洲/良心/美国等三部曲之后，拉疯已经进化到一个崭新的境界了。其次，片子的尺度根本没传言说的那么大，血腥气极低，无论是精神气质还是影像面貌，在拉疯这儿简直是“PG-13合家欢”档次。但还是太遗憾了！要不是割乳房的故事烂透了的话，本片将是我心中的神作。即便如此，它也是最好的描写连环杀手的电影之一。借由杰克（但丁）和维吉尔这组人物，将人类整体的疯狂命运批判得体无完肤。从另一个维度对接杀戮与艺术，到头来其实是完全的讽刺。本片远不是拉疯最深奥迷幻的作品，非常直白。但是各种花活玩儿的飞起，肢解爱，追求贵腐，还有“意外的幽默”，教人酣畅淋漓。最后师徒从阴道进入神曲，又通过游历地狱炼狱天堂、解构《但丁与维吉尔共渡冥河》而达到高潮，收尾也恰到好处。彩蛋更是恶趣味。'),(354,'此房是我造 The House That Jack Built(2018)','陆支羽','4.5。1.洗脑尸腥疯！2.冰窟渡往地狱的大撤退，如重返母体般令我着迷，甚至彻底消解了积蕴内心的怒火与恐惧；隔窗所闻的天堂割草声，让恶魔挤出一滴眼泪。3.男主杰克以反诹之道施行的纳粹式洗脑，反人性反家庭反女权反艺术，抱着毁灭一切的暴力姿态，跪拜贵腐之美；而路灯阴影理论的连环逻辑，甚至促使我产生“冯提尔真的为这部电影杀了61个人”的可怕幻想。4.回溯式的自我致敬那一幕，堪称迷影高潮。5.相比视觉尺度，更难熬的是心理尺度；但最为阴魂不散的，不是乳房钱包，也不是尸屋堆叠，而是那具小男孩睁眼咧嘴的微笑尸体；纵使是为讽刺病态纳粹，冯提尔的手法也够极端。'),(355,'此房是我造 The House That Jack Built(2018)','掉线','【B+】房子是杀手的自我人格，造房子便是在塑造人格，杀手一直试图造一个漂亮的木房，却总是推倒重做，便是他一直想通过杀人来建造一个正常标准内的优秀人格却屡屡失败。死神最后让他用尸体造房，也是在说：你丫压根就不是正常人。杀手入了地狱，却失望发现他的“艺术”造诣如此之高还是入不了最深层，得知还有一条几无人完成的极致之道，毫不犹豫爬上陡壁，最后和之前所有平庸的人一样掉了下去，死神无奈摊手，又一个自以为是的sb'),(356,'此房是我造 The House That Jack Built(2018)','影志','“不要看我的行为，看我的作品”纵然有很多不适，5星依然可以给导演敢挑战世俗的眼光，触碰禁忌之地，以及着魔的想法…就像在这个年代还能看到另一部《发条橙》般震撼又惊喜。'),(357,'此房是我造 The House That Jack Built(2018)','seabisuit','觉得看着爽的又没提前退场请随恋尸厌女男权纳粹拉斯疯提尔一起下光明地狱吧，恶趣味和强迫症为之一振，真想给挡字幕聊天的脑袋和不顺眼的路人一榔头，看电影也是一种围观的作恶艺术行为，创作是对观众的操控挑衅洗脑，形成共鸣或者抵触，骂和夸就像一代伟人可提高其价值也一样会成为贵腐和废墟。杀戮的剧情是得了病的导演，而狄龙与甘茨用梳理历史表达本人观点和作品宇宙的对话部分在复制了名画《但丁与维吉尔共渡冥河》的尾声治愈，找到被光照射后自己的影子停止或继续。（Fame）isaformofmasochism，（HittheroadJack）anddon’tyoucomebacknomore。（）为使用的大卫鲍伊和雷查尔斯的歌曲，还有钢琴怪才古尔德的演奏。尤伦斯欧盟影展北电标放完整版二刷'),(358,'此房是我造 The House That Jack Built(2018)','朝阳区妻夫木聪','厌女症患者拉丝疯蹄儿又一次向世人证明自己就是新时代纳粹主义的代名词，这次更是把自己致敬了一遭。开始的观感如恐怖片，而当发现自己正落入导演布下的情感陷阱时，抽离出来看反而能收获一丝畅快和幽默的愉悦。艺术的解构与重塑，宗教与人性在疯狂面前不值一提，人物沦为符号，而符号造就艺术，不断被推翻重来，在一次又一次怀疑与肯定、快感与痛苦中走向毁灭，而正如导演所言，毁灭使艺术崇高。此房是我造，天堂和地狱都由此出发，人只不过是搭建这一精神家园的材料罢了。光明与黑暗，中间隔着永恒，这部电影就是那面审视自我的镜子。拍电影就是拉丝治病的处方，他的自恋病入膏肓，而我却乐见如此。想必我大概也病的不轻。'),(359,'此房是我造 The House That Jack Built(2018)','桃桃淘电影','7。也属于电影论文，影像、概念、观点……好的地方其实很有趣，但看久了还是长，会腻。剧情本身基本就是全面展示连环杀手杀人细节和内心，部分场面还是挺大尺度的'),(360,'此房是我造 The House That Jack Built(2018)','嘟嘟熊之父','一部拉斯冯提尔献给自己的告解信、自白书、哀悼词、赞美诗。其一，导演不断推翻接纳诅咒宽慰那个孤芳自赏又无力放肆的自我，以施虐者形象完成自虐，唯有逼迫自己坠入最极致的黑暗，才能识别最细微的光。其二，导演如雕琢艺术品一般创造各式挑战观者底线的骇人情境，引诱众人释放内心潜藏已久的鬼，所有不适、麻木、惊惧与畅快都是你自身潜意识的投射。其三，所有极致的艺术家或是政治家，如站在无人之巅的不朽伟人与千古罪人，他们所瞄准的都是光年之外的遥不可及，在这条没有参照系的崎岖道路中，一切都成为模糊地带，而未知恰是最危险亦最完美的彼岸。'),(361,'此房是我造 The House That Jack Built(2018)','欧盟电影展EUFF','看似疯狂的拉斯水平稳如泰山，五段杀人先由两喜感桥段代入，再将人拖入恐怖的深渊。人类之恶的极限在何处？恶存在的意义是什么？恶能否创造艺术？爱是否是艺术的必需品？在杀手杰克与诗人维吉尔的对话中，这些主题贯穿始终，使该片成为了拉斯冯提尔迄今为止野心最大的影片。见证了一部经典诞生！'),(362,'利兹与青鸟 リズと青い鳥(2018)','DIMEYAN','迫真硬核女同动画，暂时原谅山田在《玉子市场》里面虐小绿了?。细致入微描写青春期少女间矛盾复杂感情的电影，如果是学过乐器的女同性恋观众可能会哭崩。'),(363,'利兹与青鸟 リズと青い鳥(2018)','陆秋槎','喜欢这个故事，彻底补完了伞木希美这个角色。但我总感觉，如果能遵照原作拍一季动画，效果可能会远远好过现在这个剧场版。山田监督像是在力求平淡，却终究不够克制。无限叠加同一个意味的小动作，的确是展现人物性格与内心戏的妙策，总未免有些低估观众的理解能力。想重温TV版了。'),(364,'利兹与青鸟 リズと青い鳥(2018)','efuchannel♡','喜欢京都对纤细少女情感的刻画描写，细致而生动。越是走进彼此，在意彼此，就会越害怕自己被另一方所丢下，就像希美羡慕着霙的音乐才华，霙也羡慕着开朗有人缘的希美的人格魅力一样，其实她们是彼此的莉兹，也是彼此的青鸟呢。'),(365,'利兹与青鸟 リズと青い鳥(2018)','elimu（软）','哭掉半包纸场内的肥宅也流下了泪水最喜欢反光还有喂河豚那段开头部分みぞれ耳朵竖起的表现真是绝佳｜回顾我的充满试卷的青春简直是坨?京阿尼好'),(366,'利兹与青鸟 リズと青い鳥(2018)','小油飞','太细腻了，但我对女生之间的友情到底能有多复杂并没有什么共鸣，只能把它理解成一个拉拉单恋直女的故事……我说我喜欢你的全部，你说你喜欢我的双簧管……╭(°A°`)╮'),(367,'利兹与青鸟 リズと青い鳥(2018)','琴酒','埋下分离可能的，是青鸟矫健的注定属于天空的翅膀。但如若青鸟只想跟利兹在一起而并不在意自己的翅膀，那该怎么办呢？这份不能被传递到的青鸟的心意，不明白为何自己无缘无故被看重，让希美在最后终于问出口，「何でそんなに言ってくれるのか分かんな」。于是一旦明白，比喻便失效了，盘旋于空气里的二元对立也隐匿不见。打开的鸟笼不再重要，因为自由并不意味着失去。结尾升起的那个弱音般的，“并不一定会分离”的希望，已足够坚韧。'),(368,'利兹与青鸟 リズと青い鳥(2018)','焚紙樓','她倆曾經想對彼此說的，或許只是早見沙織在《TheRollingGirls》的那句台詞：「好想和妳在一起，即便只是朋友這種關係……」也，唯有故事這種魔法，方能讓這一段在現實中只能以陰沉壓迫形容之的沉重情誼，變得如斯令人同理、催人落淚、惹人憐惜。u3000P.s.西屋太志設計的霙，實在讓人很有一種想用舌頭把她髮絲理順的下流衝動……'),(369,'利兹与青鸟 リズと青い鳥(2018)','ximo','现在最大的想法，下次见到某某某时候，对她也来个「大好きのハグ」'),(370,'利兹与青鸟 リズと青い鳥(2018)','DoubleHelix','期待了好久的京吹剧场版，除了影片刚开始时候重点给了脚步声很迷，整个电影的水平非常之高。大半个篇幅铺垫出的みぞれ的演奏相当的惊艳，泪目了直接。整体故事叙述也非常流畅，我京换了多种画风来描述这个故事，无敌。想想下一部京吹剧场版上映时候这个系列就结束了，非常的伤心。'),(371,'利兹与青鸟 リズと青い鳥(2018)','尽归去','如果你爱的人决意要离开你去远方，你会放手让她走吗？っていう話です'),(372,'利兹与青鸟 リズと青い鳥(2018)','亲爱的猥琐猪','从京吹1到2再到这部，体验了一种高光点从乐器到人、从喧嚣到私密的过渡。合奏或优雅或恢弘，演奏者各有心事，而讲述者就在反复变焦中，时而让我们聆听，时而让我们注视。这个“爱是放手”、“比翼齐飞”的故事简单得正适合京阿尼演绎，淡色水彩把空间晕染得很宽很远宛若童话里的冬季，但一个脚步一缕垂发一次眨眼一丝吐息，蕴含着比许多也有哭喊却最终欢腾依旧的青春故事立体得多的真实。这既不是一个音乐故事，也绝非简单的恋爱桥段，这可能就是一种必须做出选择的人生吧。京吹2时，就隐隐担忧着希美和霙这太过沉重的牵绊，要如何解开？暴力的？浪漫的？痛苦的？最终，《利兹与青鸟》给出了一个轻盈的答案。'),(373,'利兹与青鸟 リズと青い鳥(2018)','ample','影片的发端，少女轻扬地走在少女身前，于我们观众看去，是并立又难以了结的构图。然后山田用她现下最一贯的过度控制，让沉滞覆叠沉滞，彼此间的互相试探（追逐）的“孤悲=こい“逐渐升压，却也愈加真炽。当两人用女孩间说不上轻柔的力量拥抱在一起时，这段距离填充的是什么呢？我只能想到的是，“现在包含着过去，而又充满了未来”。所以她们直到影片结束的最后一秒，少女轻扬地跟在少女身后。'),(374,'利兹与青鸟 リズと青い鳥(2018)','颜落寒','水蓝色的制服，是某种忧郁吗？幸福的青鸟，终有一天会离开的吧？“音乐中有许多难以用音符去填满的空间，要把握在曲谱缝隙中流动的情感。”当合奏的旋律到了象征离别的某段间奏，夏天也即将接近尾声的时候，走廊里依然回响着沙沙的脚步声，孤零零的少女与笼中的青鸟始终无法忘怀一起交汇的时光。“你是我的一切，但你是自由的。”'),(375,'利兹与青鸟 リズと青い鳥(2018)','紫苑寺星流','山田惯用的“小动作”充满全片，看起来好黄啊……'),(376,'利兹与青鸟 リズと青い鳥(2018)','路西法尔','希美说自己拖累了霙，仔细想想两个人的矛盾是在这儿吗？霙根本都没表白过吧，希美又何来拖累？这好像说明二人从头到尾都不在一个频道上，霙从爱上到放手其实希美都一无所知，高潮部分以合奏作为感情交流的桥梁，既以无言胜有言而又似是而非充满歧义，恐怕是二人到底都不知该如何传达心意的缘故。表现为剧中人的“放手”，传达给观众的是“出走”。“放手”什么？“出走”哪里？百合天国破灭，京豚暴风哭泣。'),(377,'利兹与青鸟 リズと青い鳥(2018)','砂漠ノSキモ人','屏住呼吸看到字幕放完。框架是京吹精神上是声之形。少女们的表情，身体的晃动，小动作，呼吸声和脚步声，都像是透过放大镜加了麦克风一样。回过头来想，山田监督让同一个cv配莉兹和蓝鸟真的是故意的吧。最后一个cut真心喜欢。'),(378,'利兹与青鸟 リズと青い鳥(2018)','竺.不二.哼哼','静女其姝,俟我于城隅。爱而不见,搔首踟蹰。静女其娈,贻我彤管。彤管有炜,说怿女美。自牧归荑,洵美且异。匪女之为美,美人之贻。'),(379,'利兹与青鸟 リズと青い鳥(2018)','Nagi君莫思归','无法融入群体，特别的那个人就是自己世界的全部。不惜代价也希望与她在一起，眼中只有包含了对方的未来，世俗意义上的前途全然抛在脑后。仿佛看到了自己的影子，真实到令人心痛。在影院不敢出声，回到家里像失了魂一样，听着剧中的曲子，哭成了泪人。'),(380,'利兹与青鸟 リズと青い鳥(2018)','qw0aszx','如果把电影粗略分成静与动两种不同方向的风格，我觉得静风格的电影画面呈现就像是透过湖面观察湖底的样貌，那么动画做静风格的极大优势就是在于不论是湖底沙石水草的分布，还是湖水光线的清澈波动，都是能够完全被人所控制改变的。这部在日本动画里算是难得一见尝试往静的风格走的作品，并且在湖底景观的选择和放置达到近乎完美，可惜好像一定要吸引大家来看这块湖底景观一样，一会让湖水不能有一丝波澜一会又不停搅动湖水，失去了本应该有的那种自由流水所带来的折射律动。7.0分★★★☆'),(381,'利兹与青鸟 リズと青い鳥(2018)','★☆☆☆☆','（９／１０）看TV版的时候我对这两人的关注度就超过了美丽组。个人不是很喜欢TV版，而且上色略做作，相较之下剧场版的画面还是让人很舒适的，场景表现带有诗意。音乐和人物背景也配合得相当出彩。至于这个故事的结局，也许就像一开头希美所说的，略带伤感但又美好。影片是从霙的视角展开，但如果从希美的角度把剧情重新梳理一遍，就会看到这是另一个故事。影片最后，霙独自走向了音乐教室，没有像之前那样等待希美，而希美则走向了图书馆，去考取自己真正的志愿。此时两人的窗外都有鸟儿飞过，宣示着她俩互为青鸟，也互相放走了对方。'),(382,'一个明星的诞生 A Star Is Born(2018)','CummingDunham','诞生总是百转千回，毁灭却只在一瞬，而嘉嘉女士轻巧地接住了这个刹那。'),(383,'一个明星的诞生 A Star Is Born(2018)','陀螺凡达可','基本上是直接改编的76年版本，结局幸好沿用了54年版本，尽量降低了狗血度。有些改编的地方还挺有趣，比如最开始相遇的酒吧改成了GayBar而且还是DragNight。歌都很好听，Gaga表演不好不坏比较自然但碍于剧本限制，这个角色空洞苍白完全依附于男主没能立起来。前半段不错，后半段一泻千里。全片剪辑问题也特别严重。'),(384,'一个明星的诞生 A Star Is Born(2018)','thunderswallow','内部试映会看了theshallow还有清唱版本其它不想剧透最后一幕配合Iwillneverloveagain将成为农场主slay全球的一首大热抒情'),(385,'一个明星的诞生 A Star Is Born(2018)','二十二岛主','威尼斯第8场特别展映。巨星的陨落和新星的诞生，两条线都没讲好，引入的爱情婚姻线更是冲淡了影片的励志与热血，使影片变成了无聊冗长的泡沫剧。作为一部音乐电影，不是把唱歌部分拍好就能成功的，这只能是MV水平。Gaga每次唱高音部分的狰狞都会吓到我。'),(386,'一个明星的诞生 A Star Is Born(2018)','麦芽糖少年','五星配乐，一星剧情。这片子与其叫astarisborn，不如叫amiddleagelosersfantasingthathecanusehisgrossflirtingskillsmakeatalentedwomantoworshiphimlongandforever.整部片子都充斥着一个钢铁直男对于女人的意淫，毫无愧疚的把自己摆在上帝的位置上，造星只是个借口而已。作为整部片子中唯一的女人，从头到尾只是一个没有灵魂跟自我的傀儡。甚至到了最后，男人用自我陨落的方式将女人推上了神坛，可在本质上，女人仍旧是这个男人的依附品。失望至极。'),(387,'一个明星的诞生 A Star Is Born(2018)','饮歌','是没有人敢讲实话吗？好听并不代表好看啊！内核分明还是好莱坞最爱的那个“灰姑娘”，只不过这个灰姑娘穿的不是水晶鞋，她穿的是风火轮，一穿就让她在成名路上停不下来了。两个主人公感情甜蜜就飙歌，一言不合还飙歌，搞得故事根本就不连贯，人物塑造也被搁浅了。讲真，如果不是Gaga！OST值得一听倒是真的。'),(388,'一个明星的诞生 A Star Is Born(2018)','LORENZO','歌曲曲好听还热血，前半程像LaLaLand，特别爱情戏拍得都\rok，两人的甜蜜和扶持让人超级想恋爱，而且有几首歌GAGA一张口喊就哭出来。可到了苦情戏部分电影开始flop，特别是最后的Iwillneverloveagain为了追思设置的哭戏简直是电影车祸现场，烂俗至极。不过Cooper和Gaga居然演到不像本人，也有点突破了。可以预见本片还是会成为话题电影票房大卖。'),(389,'一个明星的诞生 A Star Is Born(2018)','MonaLishark','Cooper为了这样一部超长mv串烧奉献了小便失禁！！'),(390,'一个明星的诞生 A Star Is Born(2018)','メav1౿r','光是原声就slay全场，gaga跟库伯也都贡献不俗的演技。一部演烂的题材，被翻拍，能到新的高度，实属用心。'),(391,'一个明星的诞生 A Star Is Born(2018)','庞成健','我从未见过如此具有魅力的音乐题材电影'),(392,'一个明星的诞生 A Star Is Born(2018)','灰烬之地','大烂片。让人边看边想骂脏话的烂片。一个看了20分钟就想退场，之后剩下的两小时以平均10分钟看一次表的频率忍到结尾，还被烂便当和煽情嘎嘎打出两滴后悔眼泪的烂片。一星只能给唱歌。'),(393,'一个明星的诞生 A Star Is Born(2018)','BeTheOnly','看完威尼斯电影节媒体与电影人点映，这部满是音乐的电影让大早起来的我就一下子清醒起来。中规中矩的剧情，但是音乐十分走心。卸下浓妆的#LadyGaga#展现出了超自然的一面，在演技的拿捏上似乎还有一些青涩，倒也是超乎想象。'),(394,'一个明星的诞生 A Star Is Born(2018)','牛腩羊耳朵','乔安娜女士的演技我是吹不动了，大概率是剧本上男女主角内在全都空洞乏味毫无灵魂的锅，除了两者之间残存的一点点化学反应，文戏部分惨不忍睹，歌戏还是很有看头的，我憋还是呆在音乐界吧，原声歌一首首比正经专辑好听也是醉了。'),(395,'一个明星的诞生 A Star Is Born(2018)','同志亦凡人中文站','二流的故事一流的歌，Gaga本色出演，演出了倾世名伶的范儿。然而我不能理解为什么不丢掉狗血爱情的脚本，老老实实讲一个明星的诞生，看着一个为舞台而生的女孩傻大姐般的谈恋爱也是醉了...'),(396,'一个明星的诞生 A Star Is Born(2018)','UrbEStKIllrr19','铁打的Ally，开挂的人生。'),(397,'一个明星的诞生 A Star Is Born(2018)','淋','故事老掉牙，放到今天甚至可以说是庸俗。但好在整体四平八稳，嘎嘎的表现没有让人失望！踏踏实实的艺术家之路可谓曙光乍现，一张原声带展现的音乐素养slay所有同辈女歌手！PS：原声带收了！'),(398,'一个明星的诞生 A Star Is Born(2018)','十二月灬苏柒','说过了，你鳖这辈子注定要做一个全才！borninthisway！！！！'),(399,'一个明星的诞生 A Star Is Born(2018)','哪吒男','前半段特别知乎，杨超越保佑无障碍走上人生巅峰；后半段就豆瓣小组了，满地狗血内心毫无波澜甚至想笑。剧情差不多就是周迅&贾宏声的故事，但完全比不上《如果爱》。'),(400,'一个明星的诞生 A Star Is Born(2018)','.','威尼斯电影节看的，因为有上次的经验这次去得挺早直接拿了票，拿完去喝了杯咖啡回头就满场了。这IP一直听说但没看过，毕竟对美国梦式的爱情片没啥感觉，看完感觉典型的好莱坞式音乐电影，歌也就一般般吧，吹得有点过了。'),(401,'一个明星的诞生 A Star Is Born(2018)','PeterCat','第一个小时简直快把我苏死了，接下来就不知道是什么鬼了，其实后半段既然拍砸了，可以试着用马利克风格，嘀嘀咕咕配点旁白弄点逆光随便用气息直觉剪辑法来一通，哈哈哈哈哈');

/*Table structure for table `movie_info` */

DROP TABLE IF EXISTS `movie_info`;

CREATE TABLE `movie_info` (
  `id` int(11) NOT NULL AUTO_INCREMENT,
  `moviename` varchar(50) CHARACTER SET utf8 COLLATE utf8_croatian_ci DEFAULT NULL COMMENT '电影名称',
  `image` varchar(100) CHARACTER SET utf8 COLLATE utf8_croatian_ci DEFAULT NULL COMMENT '电影图片',
  `score` varchar(10) CHARACTER SET utf8 COLLATE utf8_croatian_ci DEFAULT NULL COMMENT '得分',
  `commentsamount` varchar(10) CHARACTER SET utf8 COLLATE utf8_croatian_ci DEFAULT NULL COMMENT '评论数',
  `director` varchar(50) CHARACTER SET utf8 COLLATE utf8_croatian_ci DEFAULT NULL COMMENT '导演',
  `scenario` varchar(100) CHARACTER SET utf8 COLLATE utf8_croatian_ci DEFAULT NULL COMMENT '编剧',
  `staring` varchar(300) CHARACTER SET utf8 COLLATE utf8_croatian_ci DEFAULT NULL COMMENT '主演',
  `movietype` varchar(50) CHARACTER SET utf8 COLLATE utf8_croatian_ci DEFAULT NULL COMMENT '类型',
  `releasedate` varchar(100) CHARACTER SET utf8 COLLATE utf8_croatian_ci DEFAULT NULL COMMENT '上映时间',
  `runtime` varchar(20) CHARACTER SET utf8 COLLATE utf8_croatian_ci DEFAULT NULL COMMENT '时长',
  `intro` varchar(500) CHARACTER SET utf8 COLLATE utf8_croatian_ci DEFAULT NULL COMMENT '介绍',
  PRIMARY KEY (`id`)
